import { LineChart } from "react-native-chart-kit";
import { Dimensions } from "react-native";
import { useEffect } from 'react'
import React, { useState } from 'react';
import {useDispatch, useSelector} from "react-redux"
import { StyleSheet ,TouchableOpacity, Text, View,Button, SectionList, ActivityIndicator, Modal, Alert} from 'react-native';
import {getAllSales} from "../../../../redux/salesActions"

const ventas = [0, 56, 20, 36, 80];
const videogames = [
  'Resident Evil 5',
  'The Witcher 3: Wild Hunt',
  'Tomb Raider (2013)',
  'The Elder Scrolls V: Skyrim',
  'God of War (2018)',
];

const midata = {
  labels: videogames,
  datasets: [
    {

      data: ventas,
      color: (opacity = 1) => `rgba(21, 101, 192, ${opacity})`,
      strokeWidth: 2,
    },
  ],
};

const misoptions = {
  yAxisLabel: '$',
  xAxisLabel: 'Video Games',
  chartConfig: {
    backgroundColor: '#ffffff',
    backgroundGradientFrom: '#ffffff',
    backgroundGradientTo: '#ffffff',
    decimalPlaces: 0,
    color: (opacity = 1) => `rgba(1, 87, 155, ${opacity})`,
    style: {
      borderRadius: 16,
    },

  },
};

const MyBezierLineChart = () => {
const dispatch = useDispatch()
useEffect(()=>{
  dispatch(getAllSales())
},[])
const Sales = useSelector((state)=>state.salesState)
const amounts = Sales.allSales.map(transaction => transaction.amount);
  const labels = Sales.allSales.map(transaction => {
    const date = new Date(transaction.date);
    const month = date.toLocaleString('default', { month: 'short' });
    return month;
  });

  console.log("MIIIIII AMOUNT",amounts)
  console.log("MIIIIII LABEL",labels)

  // Datos para el gr√°fico
  const data = {
    labels,
    datasets: [
      {
        data: amounts,
      },
    ],
  };

  // ===============modal filter state=========
  




  return (
   
    <LineChart
      data={data}
      width={300}
      height={220}
      chartConfig={{
        backgroundColor: '#ffffff',
        backgroundGradientFrom: '#ffffff',
        backgroundGradientTo: '#ffffff',
        decimalPlaces: 0,
        color: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
        style: {
          borderRadius: 16,
        },
      }}
      bezier
      xLabelsOffset={-10}
    />

);
};




export default MyBezierLineChart;




