import React from "react";
import {
  LineChart,
  BarChart,
  PieChart,
  ProgressChart,
  ContributionGraph,
  StackedBarChart,
} from "react-native-chart-kit";
import { Dimensions } from "react-native";

const screenWidth = Dimensions.get("window").width;

const rating = [1, 4, 3, 2, 5];
const videogames = [
  "Firewatch",
  "The Witcher 3: Wild Hunt",
  "Tomb Raider (2013)",
  "The Elder Scrolls V: Skyrim",
  "God of War (2018)",
];

const data = {
  labels: videogames,
  datasets: [
    {
      data: rating,
      color: (opacity = 1) => `rgba(21, 101, 192, ${opacity})`,
      strokeWidth: 2,
    },
  ],
};

const options = {
  yAxisLabel: "$",
  xAxisLabel: "Videogames",
  chartConfig: {
    backgroundColor: "#ffffff",
    backgroundGradientFrom: "#ffffff",
    backgroundGradientTo: "#ffffff",
    decimalPlaces: 2,
    color: (opacity = 1) => `rgba(1, 87, 155, ${opacity})`,
    style: {
      borderRadius: 16,
    },
  },
};

const PiesChart = () => {
  return (
    <PieChart
      data={data}
      width={screenWidth}
      height={220}
      chartConfig={options.chartConfig}
      backgroundColor={"transparent"}
      paddingLeft={"15"}
      center={[10, 50]}
      absolute
    />
  );
};

export default PiesChart;
