import React from 'react';
import { LineChart } from 'react-native-chart-kit';

const ventas = [0, 56, 20, 36, 80, 40, 10, 1];
const videogames = [
  'Firewatch',
  'The Witcher 3: Wild Hunt',
  'Tomb Raider (2013)',
  'The Elder Scrolls V: Skyrim',
  'God of War (2018)',
  'Terraria',
  'Warframe',
  'Grand Theft Auto IV',
];

const midata = {
  labels: videogames,
  datasets: [
    {
      data: ventas,
      color: (opacity = 1) => `rgba(21, 101, 192, ${opacity})`,
      strokeWidth: 2,
    },
  ],
};

const misoptions = {
  yAxisLabel: '$',
  xAxisLabel: 'Video Games',
  chartConfig: {
    backgroundColor: '#ffffff',
    backgroundGradientFrom: '#ffffff',
    backgroundGradientTo: '#ffffff',
    decimalPlaces: 0,
    color: (opacity = 1) => `rgba(1, 87, 155, ${opacity})`,
    style: {
      borderRadius: 16,
    },
  },
};

const LinesChart = () => {
  return (
    <LineChart
      data={midata}
      width={300}
      height={200}
      yAxisLabel="$"
      yAxisInterval={1}
      chartConfig={misoptions.chartConfig}
      bezier
    />
  );
};

const route = () =>{
  let ventas = fetch('https://gameshop-production-e844.up.railway.app/sales')
  console.log(ventas) 
}

export default LinesChart;
